import React from "react";
import { useParams } from "react-router-dom";
import HighlightedQuote from "../components/quotes/HighlightedQuote";
import Comments from "../components/comments/Comments";
import { Route, Routes, Link } from "react-router-dom";
const QuoteDetails = () => {
  const param = useParams();
  const dummy_quotes = [
    { id: "q1", author: "kareem", text: "iam learning reactjs" },
    { id: "q2", author: "kemetitch", text: "iam playing football" },
  ];
  const quote = dummy_quotes.find((quote) => quote.id === param.quoteid);
  if (!quote) {
    return <p>quote not found</p>;
  }
  return (
    <div>
      <HighlightedQuote
        text={quote.text}
        author={quote.author}
      ></HighlightedQuote>
      <div className="centered">
        <Link to="/allquotes/:quoteid//comment">Add Comment</Link>
      </div>
      <Routes>
        <Route path="/comment" element={<Comments />}></Route>
      </Routes>
    </div>
  );
};
export default QuoteDetails;
